import React, { Component } from 'react';
import cookie from "react-cookies";
import logo from './logo.svg';
import './login.css';
import $ from 'jquery';
import { Redirect } from 'react-router';
var validator = require("email-validator");

let appurl = "http://localhost:1433"

export class LoginBox extends React.Component {
  constructor(props) {
    super(props);
    this.state = { username: "", password: "", errors: [] , redirect: false};
  }
  showValidationErr(elm, msg) {
    this.setState(prevState => ({
      errors: [...prevState.errors, { elm, msg }]
    }));
  }
  clearValidationErr(elm) {
    this.setState(prevState => {
      let newArr = [];
      for (let err of prevState.errors) {
        if (elm != err.elm) {
          newArr.push(err);
        }
      }
      return { errors: newArr };
    });
  }
  onUsernameChange(param) {
    this.setState({ username: param.target.value });
    this.clearValidationErr("username");
  }
  onPasswordChange(param) {
    this.setState({ password: param.target.value });
    this.clearValidationErr("password");
  }
  submitLogin(param) {

    if (this.state.username == "") {
      this.showValidationErr("username", "username cannot be empty");
    }
    if(validator.validate(this.state.username) === false){
      this.showValidationErr("username", "Enter proper Email ID"); 
    }
    if (this.state.password == "") {
      this.showValidationErr("password", "password cannot be empty");
    }
    console.log("login going"); 
    if(this.state.username !== "" && this.state.password !== ""){
      // jquery
      $.ajax({
        url: appurl + '/account/logIn',
        method: 'POST',
        data:{
          email: this.state.username,
          password: this.state.password
        },
        success: function(result){
          if(result.login){
            console.log("login success");
            cookie.save(result.cname1, result.cvalue1, {path:"/", expires:new Date(result.cookieexpire) });
            cookie.save('uid', result.login._id, {path:"/", expires:new Date(result.cookieexpire) });
            cookie.save('isverified', result.login.verified, {path:"/", expires:new Date(result.cookieexpire) });
            cookie.save('hasextrainfo', result.login.addedExtraInfo, {path:"/", expires:new Date(result.cookieexpire) });
            cookie.save('firstname', result.login.firstName, {path:"/", expires:new Date(result.cookieexpire) });
            console.log(result.login);
            this.setState({ redirect: true });
          }
          else{
            console.log("login failed");
          }
        }.bind(this)
      });

    }
  }

  render() {
    const { redirect } = this.state;
    let usernameErr = null,
      passwordErr = null;
    for (let err of this.state.errors) {
      if (err.elm == "username") {
        usernameErr = err.msg;
      }
      if (err.elm == "password") {
        passwordErr = err.msg;
      }
    }
    if(redirect){
      console.log("redirecting");
      console.log(cookie.load('isverified'));
      console.log(cookie.load('hasextrainfo'));
      if(cookie.load('uid') !== null)
        return <Redirect to='/Home'/>
      if(cookie.load('isverified') === "false"){
        console.log("verif ma jay che");
        return <Redirect to='/login'/>
      }
      if(cookie.load('hasextrainfo') === "false"){
        console.log("extra ma jay che");
        return <Redirect to='/profile'/>
      }
      return <Redirect to='/home'/>
    }
    return (
      <div className="inner-container">
        <div className="header">Login</div>
        <div className="box">
          <div className="input-group">
            <label htmlFor="username">Username</label>
            <input
              type="email"
              name="email"
              required="true"
              className="login-input"
              placeholder="Enter Email"

              onChange={this.onUsernameChange.bind(this)}
            />
            <small className="danger-error">
              {usernameErr ? usernameErr : ""}
            </small>
          </div>

          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              name="password"
              className="login-input"
              placeholder="Enter Password"
              onChange={this.onPasswordChange.bind(this)}
            />
            <small className="danger-error">
              {passwordErr ? passwordErr : ""}
            </small>
          </div>

          <button
            type="button"
            className="login-btn"
            onClick={this.submitLogin.bind(this)}
          >
            Login
          </button>
        </div>
      </div>
    );
  }
}
