import React, { Component } from "react";
import cookie from "react-cookies";
import $ from 'jquery';

import "./img1.jpg";
import Dropdown from "react-bootstrap/Dropdown";
import AutoComplete from "./Autocomplete.js";
import "./Autocomplete.css";
import "./home.css";
import {
  Container,
  ButtonDropdown,
  DropdownMenu,
  DropdownItem,
  DropdownToggle,
  Card,
  CardImg,
  CardText,
  CardBody,
  CardHeader,
  CardTitle,
  CardSubtitle,
  Button
} from "reactstrap";
import Table from "react-bootstrap/Table";
import Avatar from "react-avatar";
import RenderToLayer from "material-ui/internal/RenderToLayer";
import "./search.css";

let appurl = "http://localhost:1433"

const color = ["red", "green", "purple", "cyan", "teal", "blue"];
const getcolor = () => {
  return color[Math.floor(Math.random() * 8)];
};

export class Home extends React.Component {
  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.select = this.select.bind(this);

    this.togglea = this.togglea.bind(this);
    this.selecta = this.selecta.bind(this);

    this.toggleb = this.toggleb.bind(this);
    this.selectb = this.selectb.bind(this);
    this.items = ["Damien", "fire", "f1", "f2", "f3", "f4", "f5", "f6"];
    this.state = {
      isBuy: false,
      isRent: true,
      isLoggedIn: cookie.load("cookiesNamejwt") ? true : false,
      dropdownOpen: false,
      value: "Number of Rooms",
      valuea: "Property Type",
      dropdownBugget: false,
      dropdownProperty: false,
      valueb: "Budget",
      token: cookie.load("cookiesNamejwt"),
      suggestions: [],
      text: "",
      searchresults: [],
    };
    console.log(document.cookie);
  }

  toggle() {
    this.setState({
      dropdownOpen: !this.state.dropdownOpen
    });
  }
  togglea() {
    this.setState({
      dropdownProperty: !this.state.dropdownProperty
    });
  }

  select(event) {
    this.setState({
      dropdownOpen: !this.state.dropdownOpen,
      value: event.target.innerText
    });
  }

  toggleb() {
    this.setState({
      dropdownBugget: !this.state.dropdownBugget
    });
  }

  selecta(event) {
    this.setState({
      dropdownProperty: !this.state.dropdownProperty,
      valuea: event.target.innerText
    });
  }

  selectb(event) {
    this.setState({
      dropdownBugget: !this.state.dropdownBugget,
      valueb: event.target.innerText
    });
  }

  onTextChanged = e => {
    const value = e.target.value;
    let suggestions = [];
    if (value.length > 0) {
      const regEx = new RegExp(`^${value}`, "i");
      suggestions = this.items.sort().filter(v => regEx.test(v));
    }
    this.setState(() => ({ suggestions, text: value }));
  };

  renderSuggestions() {
    const { suggestions } = this.state;
    if (suggestions.length === 0) {
      return null;
    } else {
      return (
        <ul>
          {suggestions.map(item => (
            <li onClick={() => this.suggestionselected(item)}>{item}</li>
          ))}
        </ul>
      );
    }
  }

  suggestionselected(value) {
    this.setState(() => ({
      text: value,
      suggestions: []
    }));
  }

  onSearch(param){
    if(this.state.text == ""){
      return;
    }
    $.ajax({
        url: appurl + '/property/searchProp',
        method: 'POST',
        data:{
          uid: cookie.load('uid'),
          searchStr: this.state.text,
          noOfRooms: this.state,value,
          property_amount: this.state,valueb,
          property_type: this.state,valuea,

        },
        success: function(result){
          console.log(result.searchResult);
          this.setState({ searchresults: result.searchResult});
          console.log("searched properties " + this.state.searchresults);
        }.bind(this)
      });
