import React, { PureComponent } from "react";
import cookie from "react-cookies";
import "./ViewProfile.css";
import $ from 'jquery';

let appurl = "http://localhost:1433"

export class EditProfile extends PureComponent {
  constructor(props) {
    super(props);
    
    this.onGetUser = this.onGetUser.bind(this);
    let temp = "";
    let userstatus = 0;
    $.ajax({
        url: appurl + '/account/getUser',
        method: 'POST',
        data:{
          userId: cookie.load("uid")
        },
        statusCode: {
          200: function(){
            console.log("user retrived success");
            userstatus = 200;
          }
        },
        success: function(result){
          temp = result.user.userName;
          // this.state.firstname = result.user.firstName;
          // this.setState({lastname: result.user.lastname});
          // this.setState({gender: result.user.sex});
          // this.setState({mobile: result.user.mobileno});
          // this.setState({address: result.user.address});
          // this.setState({country: result.user.country});
          // this.setState({state: result.user.state});
          // this.setState({city: result.user.city});
          // this.setState({country: result.user.country});
        },
        error: function (result){
          console.log("user retrived failed");
        }
      });


      this.state = {
        firstname: temp,
        uid: cookie.load("uid"),
        contactno: "",
        firstname: "",
        lastname: "",
        gender: "",
        mobile: "",
        address: "",
        country: "",
        state: "",
        city: "",
        pincode: ""
      };


    /*this.handleChange = handleChange.bind(this);*/
  }

  componentWillMount(){
    this.onGetUser();
    // const userstatus = 0;
    // $.ajax({
    //     url: appurl + '/account/getUser',
    //     method: 'POST',
    //     data:{
    //       userId: this.state.uid
    //     },
    //     statusCode: {
    //       200: function(){
    //         console.log("user retrived success");
    //         userstatus = 200;
    //       }
    //     },
    //     success: function(result){
    //       this.setState({firstname: result.user.firstname});
    //       this.setState({lastname: result.user.lastname});
    //       this.setState({gender: result.user.sex});
    //       this.setState({mobile: result.user.mobileno});
    //       this.setState({address: result.user.address});
    //       this.setState({country: result.user.country});
    //       this.setState({state: result.user.state});
    //       this.setState({city: result.user.city});
    //       this.setState({country: result.user.country});
    //     },
    //     error: function (result){
    //       console.log("user retrived failed");
    //     }
    //   });
  }

  onEdit(param){
    console.log("editing");
    console.log(this.state.firstname);

    $.ajax({
        url: appurl + '/account/UpdateUser',
        method: 'POST',
        data:{
          userId: this.state.uid,
          firstname: this.state.firstname,
          lastname: this.state.lastname,
          sex: this.state.gender,
          contactno: this.state.mobile,
          address: this.state.address,
          country: this.state.country,
          state: this.state.state,
          district: "null",
          city: this.state.city,
          pincode: this.state.pincode
        },
        success: function(result){
          if(result.login){
            console.log("update success");
          }
          else{
            console.log("update failed");
          }
        }.bind(this)
      });

  }

  onFirstnameChange(param) {
    this.setState({ firstname: param.target.value });
  }
  onLastnameChange(param) {
    this.setState({ lastname: param.target.value });
  }
  onGenderChange(param) {
    this.setState({ gender: param.target.value });
  }
  onMobilenumberChange(param) {
    this.setState({ mobile: param.target.value });
  }
  onAddressChange(param) {
    this.setState({ address: param.target.value });
  }
  onCountryChange(param) {
    this.setState({ country: param.target.value });
  }
  onStateChange(param) {
    this.setState({ state: param.target.value });
  }
  onCityChange(param) {
    this.setState({ city: param.target.value });
  }
  onPincodeChange(param) {
    this.setState({ pincode: param.target.value });
  }

  onGetUser(){
    console.log("calling");
    // const userstatus = 0;
    // $.ajax({
    //     url: appurl + '/account/getUser',
    //     method: 'POST',
    //     data:{
    //       userId: this.state.uid
    //     },
    //     statusCode: {
    //       200: function(){
    //         console.log("user retrived success");
    //         userstatus = 200;
    //       }
    //     },
    //     success: function(result){
    //       this.setState({firstname: result.user.firstname});
    //       this.setState({lastname: result.user.lastname});
    //       this.setState({gender: result.user.sex});
    //       this.setState({mobile: result.user.mobileno});
    //       this.setState({address: result.user.address});
    //       this.setState({country: result.user.country});
    //       this.setState({state: result.user.state});
    //       this.setState({city: result.user.city});
    //       this.setState({country: result.user.country});
    //     },
    //     error: function (result){
    //       console.log("user retrived failed");
    //     }
    //   });
    }
  


  render() {    
    const { firstname, uid, contactno, lastname, gender, mobile, address, country, state, city, pincode } = this.state;

    return (
      <div>
        <form
          className="edit-profile"
          /* onSubmit={e => {
            e.preventDefault();
            this.props.changeIsEdit();
            this.props.updateUser(this.state);
          }}*/
        >
          <table className="table table-striped animated fadeIn">
            <tbody>
              <tr>
                <td> First Name</td>
                <td>
                  <input
                    type="text"
                    placeholder="Enter you first name"
                    name="firstname"
                    required="true"
                    value={firstname}
                    onChange={this.onFirstnameChange.bind(this)}
                  />
                </td>
              </tr>
              <tr>
                <td> Last Name</td>
                <td>
                  <input
                    type="text"
                    placeholder="Enter you last name"
                    name="lastname"
                    required="true"
                    value={lastname}
                    onChange={this.onLastnameChange.bind(this)}
                  />
                </td>
              </tr>
              <tr>
                <td>Gender</td>
                <td>
                  <input
                    type="text"
                    placeholder="Enter you gender"
                    name="gender"
                    required="true"
                    onChange={this.onGenderChange.bind(this)}
                  />
                </td>
              </tr>
              <tr>
                <td>Primary Email</td>
                <td>{this.state.email}</td>
              </tr>
              <tr>
                <td> Mobile Number </td>
                <td>
                  <input
                    type="text"
                    placeholder="Enter Mobile Number"
                    name="contactNo"
                    required="true"
                    pattern="[0-9]{10}"
                    onChange={this.onMobilenumberChange.bind(this)}
                  />
                </td>
              </tr>
              <tr>
                <td>Address</td>
                <td>
                  <input
                    type="text"
                    placeholder="Enter you Address"
                    name="address"
                    required="true"
                    onChange={this.onAddressChange.bind(this)}
                  />
                </td>
              </tr>
              <tr>
                <td>City</td>
                <td>
                  <input
                    type="text"
                    placeholder="Enter you city"
                    name="city"
                    required="true"
                    onChange={this.onCityChange.bind(this)}
                  />
                </td>
              </tr>
              <tr>
                <td>State</td>
                <td>
                  <input
                    type="text"
                    placeholder="Enter your state"
                    name="state"
                    required="true"
                    onChange={this.onStateChange.bind(this)}
                  />
                </td>
              </tr>
              <tr>
                <td>Country</td>
                <td>
                  <input
                    type="text"
                    placeholder="Enter your Country"
                    name="state"
                    required="true"
                    onChange={this.onCountryChange.bind(this)}
                  />
                </td>
              </tr>
              <tr>
                <td>Pincode</td>
                <td>
                  <input
                    type="numeric"
                    placeholder="Enter your state"
                    name="state"
                    required="true"
                    onChange={this.onPincodeChange.bind(this)}
                  />
                </td>
              </tr>
            </tbody>
          </table>
          <input
            type="submit"
            className="btn btn-outline-success style-btn"
            value="Save"
            onClick={this.onEdit.bind(this)}
          />
        </form>
      </div>
    );
  }
}
